mapper.select:
SELECT goods_name goodsName, sum(${columeName}) ${columeName} FROM shop_goods_log WHERE store_id = #{storeId}
AND DATE_FORMAT(addTime,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
AND DATE_FORMAT(addTime,'%Y-%m-%d') < DATE_FORMAT(#{endTime},'%Y-%m-%d')
GROUP BY goods_id ORDER BY sum(${columeName}) ${orderType} limit ${start}, ${offset}
