mapper.select:
SELECT obj.id, obj.goods_name, obj.goods_current_price, obj.goods_price, obj.goods_main_photo_path, obj.goods_index_photo_path, obj.goods_inventory_detail, obj.inventory_type FROM shop_goods obj WHERE obj.goods_status = 0 AND obj.delete_status = 0
AND obj.gc_id = #{gcId}
AND obj.id != #{goodsId}
ORDER BY obj.goods_salenum DESC

select g.goods_brand_id, store_name, g.id, g.goods_name, g.inventory_type, g.goods_inventory_detail, g.goods_current_price, g.goods_price, g.goods_salenum, g.goods_main_photo_path, g.goods_index_photo_path, store.store_second_domain, g.self_add_price, g.goods_type, g.cn_self_add_price, g.ex_price_flag from shop_goods g left join shop_store store ON store.id=g.goods_store_id LEFT JOIN shop_goods_brand gb ON g.goods_brand_id = gb.id where g.delete_status = 0
and g.goods_status = #{goodsStatus}
and g.id in
#{listItem}
and g.id != #{notId}
and g.gc_id = #{gcId}
and g.goods_store_id = #{goodsStoreId}
and g.store_recommend = #{storeRecommend}
and g.goods_brand_id = #{goodsBrandId}
and gb.name like '%${likebBrandName}%'
order by ${orderBy} ${orderType}
order by field(g.id,
#{listItem}
)
limit 0,#{count}

select g.*, b.`name` brandName , u.`name` userName
, store.*
from shop_goods g
INNER JOIN shop_goods_ugc gugc on gugc.goods_id = g.id AND gugc.class_id = #{vo.userGoodsClassId}
inner join shop_store store ON store.id=g.goods_store_id and store.store_name like CONCAT('%', #{vo.storeName} ,'%')
left join shop_store store ON store.id=g.goods_store_id
LEFT JOIN shop_goods_class sc1 ON (sc1.id = g.gc_id AND sc1.level=2) LEFT JOIN shop_goods_class sc2 ON ((sc1.parent_id = sc2.id OR sc2.id = g.gc_id) AND sc2.level=1) LEFT JOIN shop_goods_class sc3 ON ((sc2.parent_id = sc3.id) AND sc3.level=0) left join shop_goods_brand b on g.goods_brand_id = b.id left join user_manager u on g.user_admin_id = u.uid where g.delete_status = 0
AND g.goods_status = #{vo.goodsStatus}
AND g.goods_status in
#{status}
AND g.goods_name like CONCAT('%', #{vo.goodsName} ,'%')
AND g.goods_type = #{vo.goodsType}
AND g.goods_brand_id = #{vo.goodsBrandId}
AND g.user_admin_id = #{vo.userAdminId}
AND g.gc_id = #{vo.gcId}
AND sc3.id=#{vo.fgcId}
AND sc2.id = #{vo.sgcId}
AND sc1.id = #{vo.tgcId}
AND g.goods_store_id = #{vo.storeId}
AND g.gc_id in
#{gcId}
AND DATE_FORMAT(g.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{vo.startDate},'%Y-%m-%d')
AND DATE_FORMAT(g.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{vo.endDate},'%Y-%m-%d')
AND g.add_time >= DATE_FORMAT(now(),'%Y-%m-%d 00:00:00') and g.add_time <= DATE_FORMAT(now(),'%Y-%m-%d 23:59:59')
AND g.add_time >= DATE_FORMAT(date_sub(current_date(),interval 1 day),'%Y-%m-%d 00:00:00') and g.add_time <= DATE_FORMAT(date_sub(current_date(),interval 1 day),'%Y-%m-%d 23:59:59')
AND g.add_time >= concat(#{vo.startTime},' 00:00:00') and g.add_time <= concat(#{vo.endTime},' 23:59:59')
AND g.id = #{vo.goodsId}
AND g.is_ygb = #{vo.isYgb}
order by g.add_time desc

SELECT g.id, g.goods_name, g.inventory_type, g.goods_inventory_detail, g.goods_current_price, g.goods_price, g.goods_main_photo_path, g.goods_index_photo_path, g.has_exchange_integral, g.has_mobile_price, g.goods_salenum, g.exchange_integral, g.goods_integral_price, g.goods_mobile_price, g.goods_transfee, g.self_add_price, g.goods_type, g.cn_self_add_price, g.ex_price_flag FROM shop_goods g
INNER JOIN shop_goods_ugc ugc ON ugc.goods_id = g.id AND ugc.class_id in
#{ugcId}
WHERE g.goods_status = 0 and g.delete_status=0
AND g.gc_id in
#{gcId}
AND g.goods_cod = #{vo.goodsCod}
AND g.goods_transfee = #{vo.goodsTransfee}
AND g.goods_brand_id in
#{gbId}
AND g.id in(SELECT goods_id FROM shop_goods_spec gs WHERE gs.spec_id in
#{gsId}
)
AND (g.goods_property LIKE CONCAT('%',#{vo.gtpName}, '%') AND g.goods_property LIKE CONCAT('%',#{vo.gtpValue}, '%'))
AND g.goods_inventory > 0
AND g.goods_store_id = #{vo.storeId}
AND g.goods_type = #{vo.goodsType}
AND g.is_ygb = ${vo.isYgb}
AND g.ex_price_flag=1 AND g.cn_self_add_price > 0
AND g.goods_inventory > 0
ORDER BY g.${vo.orderBy} ${vo.orderType}

