mapper.select:
SELECT
,
FROM shop_integral_goods_order sigo LEFT JOIN sys_user_account uc ON (uc.uid = sigo.igo_user_id and uc.account_type=2)
sigo.igo_order_sn like concat('%',#{vo.igoOrderSn},'%')
AND uc.login_name like concat('%',#{vo.userName},'%')
AND sigo.igo_user_id=#{vo.uid}
AND DATE_FORMAT(add_time,'%Y-%m-%d') >= #{vo.beginTime}
AND DATE_FORMAT(add_time,'%Y-%m-%d') <= #{vo.endTime}
AND (igo_payment = 'alipay' or igo_payment = 'alipay_wap' or igo_payment = 'alipay_app')
AND (igo_payment = 'wx_app' or igo_payment = 'wx_pay' or igo_payment='wxqrpay')
AND igo_payment = #{vo.igoPayment}
AND sigo.igo_status = #{vo.igoStatus}
AND sigo.igo_status = 0
ORDER BY sigo.add_time desc

SELECT
,
, c.email FROM shop_integral_goods_order sigo LEFT JOIN sys_user_account uc ON (uc.uid = sigo.igo_user_id and uc.account_type=2) LEFT JOIN user_customer c ON sigo.igo_user_id = c.uid WHERE sigo.id = #{id}

SELECT sum(sigo.igo_trans_fee)
FROM shop_integral_goods_order sigo
LEFT JOIN sys_user_account uc ON (uc.uid = sigo.igo_user_id and
uc.account_type=2)


		sigo.igo_order_sn like concat('%',#{vo.igoOrderSn},'%')


		AND uc.login_name like concat('%',#{vo.userName},'%')


		AND sigo.igo_user_id=#{vo.uid}



AND DATE_FORMAT(add_time,'%Y-%m-%d') >= #{vo.beginTime}


AND DATE_FORMAT(add_time,'%Y-%m-%d') <= #{vo.endTime}





				AND (igo_payment = 'alipay' or igo_payment = 'alipay_wap' or
				igo_payment = 'alipay_app')


				AND (igo_payment = 'wx_app' or igo_payment = 'wx_pay')


				AND igo_payment = #{vo.igoPayment}




		AND sigo.igo_status = #{vo.igoStatus}



ORDER BY sigo.add_time desc

SELECT sum(sigo.igo_total_integral)
FROM shop_integral_goods_order sigo
LEFT JOIN sys_user_account uc ON (uc.uid = sigo.igo_user_id and
uc.account_type=2)


		sigo.igo_order_sn like concat('%',#{vo.igoOrderSn},'%')


		AND uc.login_name like concat('%',#{vo.userName},'%')


		AND sigo.igo_user_id=#{vo.uid}



AND DATE_FORMAT(add_time,'%Y-%m-%d') >= #{vo.beginTime}


AND DATE_FORMAT(add_time,'%Y-%m-%d') <= #{vo.endTime}





				AND (igo_payment = 'alipay' or igo_payment = 'alipay_wap' or
				igo_payment = 'alipay_app')


				AND (igo_payment = 'wx_app' or igo_payment = 'wx_pay')


				AND igo_payment = #{vo.igoPayment}




		AND sigo.igo_status = #{vo.igoStatus}



ORDER BY sigo.add_time desc
