mapper.select:
select

	distinct


from shop_predeposit_cash




	order by ${orderByClause}


	limit ${limit}

select
,
, ( SELECT NAME FROM user_manager WHERE user_manager.uid = shop_predeposit_cash.cash_admin_id ) AS adminname, ( SELECT CASE user_code WHEN 4 THEN (SELECT o.user_name FROM user_operator o WHERE o.user_id = u.id) WHEN 3 THEN (SELECT o.user_name FROM user_operator o WHERE o.user_id = u.id) ELSE (SELECT nickname FROM user_customer c WHERE c.uid = u.id) END AS nickname FROM sys_user u WHERE u.id = shop_predeposit_cash.cash_user_id ) AS nickname, ( SELECT CASE user_code WHEN 4 THEN (SELECT o.phone FROM user_operator o WHERE o.user_id = u.id) WHEN 3 THEN (SELECT o.phone FROM user_operator o WHERE o.user_id = u.id) ELSE (SELECT login_name FROM sys_user_account c WHERE c.uid = u.id and c.account_type=2) END AS mobile FROM sys_user u WHERE u.id = shop_predeposit_cash.cash_user_id ) AS mobile FROM shop_predeposit_cash LEFT JOIN user_customer b ON shop_predeposit_cash.cash_user_id = b.uid where id = #{id,jdbcType=BIGINT}

select

from shop_predeposit_cash where cash_user_id=#{userId} order by
add_time desc limit 1;

select

from shop_predeposit_cash where cash_user_id=#{userId}

	and cash_sn like concat('%',#{vo.cashSn},'%')


	and cash_status=#{vo.cashStatus}


	and cash_payment=#{vo.cashPayment}


	and cash_pay_status=#{vo.cashPayStatus}


	and DATE_FORMAT(add_time,'%Y-%m-%d')  >=
	DATE_FORMAT(#{vo.startDate},'%Y-%m-%d')


	and DATE_FORMAT(add_time,'%Y-%m-%d')  <=
	DATE_FORMAT(#{vo.endDate},'%Y-%m-%d')


	AND user_code = #{vo.userCode}

order by add_time desc

select SUM(cash_amount) from shop_predeposit_cash where 1=1 and (cash_status = 1 OR cash_status = 0 )
AND cash_user_id = #{vo.cashUserId}
AND user_code = #{vo.userCode}
AND DATE_FORMAT(shop_predeposit_cash.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{vo.beginTime},'%Y-%m-%d')
AND DATE_FORMAT(shop_predeposit_cash.add_time,'%Y-%m-%d') <= DATE_FORMAT(#{vo.endTime},'%Y-%m-%d')
AND (cash_poundage = 0 OR cash_poundage IS NULL)
