mapper.select:
SELECT SUM(total_fee) FROM shop_qr_pay_order WHERE pay_status=1
and seller_id = #{sellerId}
AND DATE_FORMAT(create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d')
AND DATE_FORMAT(create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
;

mapper.update:
update
shop_qr_pay_order

	pay_status = #{obj.payStatus,jdbcType=TINYINT}


	1=1

		and seller_id = #{obj.sellerId,jdbcType=BIGINT}


		and pay_order_no = #{obj.payOrderNo,jdbcType=VARCHAR}


		and payment_type = #{obj.paymentType,jdbcType=TINYINT}
