mapper.select:
select max(end_score)
from check_group_result
where group_id = #{groupId}

SELECT COUNT(1) FROM user_check_result_history
WHERE 1=1

	and user_id = #{userId,jdbcType = BIGINT}


	and group_id = #{groupId,jdbcType = BIGINT}

AND DATE_FORMAT(create_time ,'%Y-%m-%d') = DATE_FORMAT(NOW()
,'%Y-%m-%d')
