mapper.select:
select

from sys_ad_content where 1=1

	and id = #{vo.id}


	and area_ad_id = #{vo.areaAdId}


	and parent_id = #{vo.parentId}


	and area_id = #{vo.areaId}


	and name = #{vo.name}


	and type = #{vo.type}


	and disabled = #{vo.disabled}


	and create_by = #{vo.createBy}


	and parent_id is null

order by orders asc

		select 
		,style
		from (
		select 
		from
		sys_area_ad a left join sys_ad_content c on a.id =c.area_ad_id
		where c.disabled = 0 and a.disabled = 0 and a.code = #{code}


           and c.area_id = #{areaId}


           and c.area_id is null


		group by c.parent_id
		union
		select  
	    from sys_area_ad a left join
		sys_ad_content c on a.id =
		c.area_ad_id where a.code = #{code}
		and c.area_id is null and c.disabled = 0 and a.disabled = 0
		and c.id not in (select c.parent_id from sys_area_ad a left join
		sys_ad_content c on a.id = c.area_ad_id where c.disabled = 0 and
		a.disabled = 0 and a.code = #{code}


           and c.area_id = #{areaId}


           and c.area_id is null


		)) ad order by orders

SELECT sac1.id,sac1.area_ad_id,sac2.name as dname,sac1.id as did,da.area_Name,sac1.pic,saa.name,sac1.disabled FROM `sys_ad_content` sac1 LEFT JOIN `sys_ad_content` sac2 ON sac1.parent_id=sac2.id LEFT JOIN dic_area da ON da.id = sac1.area_id LEFT JOIN sys_area_ad saa ON saa.id = sac1.area_ad_id where sac1.parent_id IS NOT NULL
AND sac2.id= #{vo.id}
AND sac1.area_ad_id = #{vo.areaAdId}
order by sac1.orders asc

select

  distinct


from sys_ad_content




  order by ${orderByClause}


  limit ${limit}
