mapper.select:
SELECT c.class_Name,a.* FROM sys_article a,sys_articleclass c WHERE
a.article_Class_id=c.id AND c.id=#{id} and a.`type`=#{user}
order by a.add_Time desc

select a.id, a.add_Time, a.delete_Status, a.display, a.mark, a.sequence,a.title, a.type, a.url, a.article_Class_id FROM sys_article a LEFT JOIN `sys_articleclass` ac ON ac.`id` = a.`article_Class_id`
ac.mark = #{mark,jdbcType=VARCHAR}
order by a.add_Time desc

select

,ac.class_Name
from sys_article a
INNER join sys_articleclass ac on a.article_Class_id =ac.id
AND ac.mark not in ('sellernotice','o2osellernotice','operatornotice',
'gongsidongtai')
AND ac.mark not like 'ZS_%'


		and a.add_Time > #{gtAddTime}


		and a.add_Time < #{ltAddTime}


		and a.article_Class_id =#{classId}


		and a.type = #{type}


		and a.display = #{display}


ORDER BY a.add_Time DESC,a.sequence ASC 

	limit 0,#{count}

select

,ac.class_Name
from sys_article a
INNER join sys_articleclass ac on a.article_Class_id =ac.id
AND ac.mark not in ("sellernotice","o2osellernotice","operatornotice")


		and a.add_Time > #{vo.gtAddTime}


		and a.add_Time < #{vo.ltAddTime}


		and a.article_Class_id =#{vo.classId}


		and a.article_Class_id in

			#{listItem}



		and a.type = #{vo.type}


		and a.display = #{vo.display}


ORDER BY a.add_Time DESC,a.sequence ASC

SELECT

FROM sys_article
WHERE article_Class_id = #{classId}
AND delete_Status = 0

	AND type = #{type}

ORDER BY add_Time DESC,sequence ASC

SELECT

,a.content,ac.class_Name
FROM sys_article a
INNER join sys_articleclass ac on a.article_Class_id= ac.id AND ac.mark not
in ("sellernotice","o2osellernotice","operatornotice")
inner join sys_articleclass pac on ac.parent_id=pac.id
WHERE a.delete_Status = 0
and a.display=#{display}

	AND type = #{type}


	and pac.mark in

		#{listItem}


ORDER BY add_Time DESC

	limit 0,#{count}

