mapper.select:
select uc.nickname as user_name, ur.create_time, (SELECT SUM(rebate_amount) FROM user_rebate_record rr INNER JOIN o2o_group_order ord on ord.id = rr.order_id WHERE rr.user_id = #{userId} AND ord.user_id = ur.user_id) as rebate_amount from user_relation ur left join user_customer uc on uc.uid = ur.user_id where ur.referee_user_id = #{userId}
UNION ALL
SELECT CONCAT('微信:',wr.nick_name) AS user_name, wr.create_time, (SELECT SUM(rebate_amount) FROM user_rebate_record rr INNER JOIN o2o_group_order AS ORD ON ord.id = rr.order_id WHERE rr.user_id = #{userId} AND ord.user_id = ua.uid) AS rebate_amount FROM wx_invitation_relation wr LEFT JOIN `sys_user_account` ua ON ua.account_type = 4 AND ua.login_name = wr.union_id LEFT JOIN user_relation ur ON ur.`user_id` = ua.uid LEFT JOIN user_customer uc ON uc.uid = ua.uid WHERE IFNULL(ur.referee_user_id,'') = '' AND wr.owner_id = #{userId}

select u.* from sys_user u INNER join sys_user_account ua on
u.id=ua.uid
where u.is_deleted=0 and ua.login_name=#{loginName}
