mapper.select:
SELECT ps.*, pt.type_name physicalTypeName FROM check_physical_suggest
ps
LEFT JOIN check_physical_type pt ON ps.physical_type_id = pt.id


		and ps.physical_type_id = #{vo.physicalTypeId}


		and suggest_type = #{vo.suggestType}


		and is_enabled = #{vo.isEnabled}

SELECT * FROM check_physical_suggest

	1 =1

		and id = #{vo.id,jdbcType=BIGINT}


		and physical_type_id = #{vo.physicalTypeId,jdbcType=BIGINT}


		and result_type = #{vo.resultType,jdbcType=INTEGER}


		and suggest_type = #{vo.suggestType,jdbcType=INTEGER}


		and is_enabled = #{vo.isEnabled,jdbcType=INTEGER}


		and delete_status = #{vo.deleteStatus,jdbcType=TINYINT}


		and create_by = #{vo.createBy,jdbcType=BIGINT}


		and create_time = #{vo.createTime,jdbcType=TIMESTAMP}


		and modify_by = #{vo.modifyBy,jdbcType=BIGINT}


		and modify_time = #{vo.modifyTime,jdbcType=TIMESTAMP}

	order by suggest_type asc,priority asc
