mapper.select:
SELECT u.user_type,r.* FROM user_relation r
INNER JOIN sys_user u ON r.user_id=u.id
WHERE r.user_id=#{uid}

SELECT su.invitation_code FROM user_relation ur LEFT JOIN sys_user su ON
ur.referee_user_id = su.id
WHERE ur.user_id = #{uid}

SELECT count(distinct o.user_id) as total_amout FROM user_relation o
WHERE
o.referee_user_id=#{refereeUserId} and (o.user_id != o.seller_user_id or o.seller_user_id IS NULL)

AND date(o.create_time) >=#{startDate,jdbcType=TIMESTAMP}


AND date(o.create_time) <= #{endDate,jdbcType=TIMESTAMP}

SELECT count(distinct o.user_id) as total_amout FROM user_relation o
WHERE o.seller_user_id = #{sellerUserId}

AND date(o.create_time) >=#{startDate,jdbcType=TIMESTAMP}


AND date(o.create_time) <= #{endDate,jdbcType=TIMESTAMP}

SELECT count(distinct o.user_id) as total_amout FROM user_relation o
WHERE o.referee_user_id=#{refereeUserId} and o.user_id =
o.seller_user_id

AND date(o.create_time) >=#{startDate,jdbcType=TIMESTAMP}


AND date(o.create_time) <= #{endDate,jdbcType=TIMESTAMP}

SELECT COUNT(referee_user_id) FROM user_relation WHERE referee_user_id
IN
(SELECT uid FROM sys_user_account WHERE login_name
IN

	#{item}

)

	AND date(user_relation.create_time) >=#{startDate,jdbcType=TIMESTAMP}


	AND date(user_relation.create_time) <= #{endDate,jdbcType=TIMESTAMP}

SELECT COUNT(referee_user_id) FROM user_relation WHERE referee_user_id
IN (SELECT user_id FROM user_operator WHERE cn_username
IN

	#{item}

)

	AND date(user_relation.create_time) >=#{startDate,jdbcType=TIMESTAMP}


	AND date(user_relation.create_time) <= #{endDate,jdbcType=TIMESTAMP}

SELECT COUNT(referee_user_id) FROM user_relation WHERE referee_user_id
IN
(
SELECT uid FROM `user_seller` us LEFT JOIN `shop_store` ss ON
(ss.id=us.store_id AND is_o2o = 1)
WHERE us.uid IN( SELECT uid FROM sys_user_account WHERE login_name in

	#{item}

)
)

	AND date(user_relation.create_time) >=#{startDate,jdbcType=TIMESTAMP}


	AND date(user_relation.create_time) <= #{endDate,jdbcType=TIMESTAMP}

mapper.update:
update user_relation
set referee_user_id = #{newRefereeUid,jdbcType=BIGINT}
where referee_user_id = #{oldRefereeUid,jdbcType=BIGINT}

update user_relation
set seller_user_id = #{newUid,jdbcType=BIGINT}
where seller_user_id = #{oldUid,jdbcType=BIGINT}
