mapper.insert:
insert into user_step_record (id, uid, step_number,
record_date, total_Km, calories
)
values (#{id,jdbcType=BIGINT}, #{uid,jdbcType=BIGINT},
#{stepNumber,jdbcType=INTEGER},
#{recordDate,jdbcType=DATE}, #{totalKm,jdbcType=DECIMAL}, #{calories,jdbcType=DECIMAL}
)

mapper.select:
select

from user_step_record where uid=#{uid,jdbcType=BIGINT} AND
record_date=#{recordDate,jdbcType=DATE}
