mapper.select:
select

	distinct


from glod_egg_record




	order by ${orderByClause}


	limit ${limit}

select

from
glod_egg_record
where
1=1

	and uid = #{vo.uid,jdbcType=BIGINT}


	and money = #{vo.money,jdbcType=DECIMAL}


	and create_time = #{vo.createTime,jdbcType=TIMESTAMP}


	and cn_account = #{vo.cnAccount,jdbcType=VARCHAR}


	and mobile = #{vo.mobile,jdbcType=VARCHAR}


	and nick_name = #{vo.nickName,jdbcType=VARCHAR}


	and app_name = #{vo.appName,jdbcType=VARCHAR}


	and egg_index = #{vo.eggIndex,jdbcType=TINYINT}


	and glod_egg_id = #{vo.glodEggId,jdbcType=BIGINT}


AND date(create_time) >=#{vo.beginTime,jdbcType=TIMESTAMP}


AND date(create_time) <= #{vo.endTime,jdbcType=TIMESTAMP}
