mapper.select:
select

	distinct


,

from shop_goods_case




	order by ${orderByClause}


	limit 0,#{count}

mapper.insert:
insert into shop_goods_case (id, add_time, delete_status,
case_id, case_name, display,
sequence, other_case, case_content
)
values (#{id,jdbcType=BIGINT}, #{addTime,jdbcType=TIMESTAMP},
#{deleteStatus,jdbcType=BIT},
#{caseId,jdbcType=VARCHAR}, #{caseName,jdbcType=VARCHAR}, #{display,jdbcType=INTEGER},
#{sequence,jdbcType=INTEGER}, #{otherCase,jdbcType=INTEGER},
#{caseContent,jdbcType=LONGVARCHAR}
)

mapper.select:
select
,
from shop_goods_case where 1=1
AND display=#{vo.display}
AND other_case=#{vo.otherCase}
order by sequence
