mapper.select:
select
gc.*,gt.name as goods_type_name
from shop_goods_class gc
left join shop_goods_type gt on gc.goods_type_id =gt.id
where gc.delete_status=0

	and gc.parent_id =#{parentId}


	and gc.parent_id is null


	and gc.display=#{display}


	and gc.recommend=#{recommend}


	and gc.level=#{level}

order by gc.sequence asc

	limit 0,#{count}

select
gc.*,gt.name as goods_type_name
from shop_goods_class gc
left join shop_goods_type gt on gc.goods_type_id =gt.id
where gc.delete_status=0 and display = 1

	and gc.parent_id =#{parentId}


	and gc.display=#{display}


	and gc.recommend=#{recommend}


	and gc.level=#{level}

order by gc.sequence asc

	limit 0,#{count}

select

  distinct


from shop_goods_class




  order by ${orderByClause}


  limit ${limit}
