mapper.select:
select

from shop_goods_specification
where delete_Status = 0

	and spec_type = #{specType}


	and store_id = #{storeId}

order by sequence asc

mapper.insert:
insert into shop_goods_specification (id, add_Time, delete_Status,
name, sequence, spec_type,
type, goods_class_id, store_id
)
values (#{id,jdbcType=BIGINT}, #{addTime,jdbcType=TIMESTAMP},
#{deleteStatus,jdbcType=INTEGER},
#{name,jdbcType=VARCHAR}, #{sequence,jdbcType=INTEGER}, #{specType,jdbcType=INTEGER},
#{type,jdbcType=VARCHAR}, #{goodsClassId,jdbcType=BIGINT},
#{storeId,jdbcType=BIGINT}
)

mapper.select:
select

from shop_goods_specification
where delete_Status = 0

	and name like CONCAT('%', #{vo.name} ,'%')


	and spec_type = #{vo.specType}


	and store_id = #{vo.storeId}



	and delete_status = #{vo.deleteStatus}



	and display = #{vo.display}


order by sequence asc
