  delete_status, orderno, ordertime, 
  subchannel, platid, goodsnumber, 
  goodsmoney, user_id, goodslist
  )
values (#{id,jdbcType=BIGINT}, #{addTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{deleteStatus,jdbcType=INTEGER}, #{orderno,jdbcType=VARCHAR}, #{ordertime,jdbcType=TIMESTAMP}, 
  #{subchannel,jdbcType=VARCHAR}, #{platid,jdbcType=INTEGER}, #{goodsnumber,jdbcType=BIGINT}, 
  #{goodsmoney,jdbcType=DECIMAL}, #{userId,jdbcType=BIGINT}, #{goodslist,jdbcType=LONGVARCHAR}
  )

