mapper.select:
SELECT
FROM shop_store_group_adjust s where 1=1
and s.store_id = #{vo.storeId,jdbcType=BIGINT}

SELECT
FROM shop_store_group_adjust s
and s.store_name = #{vo.storeName}
and s.apply_status = #{vo.applyStatus}
AND DATE_FORMAT(s.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{vo.beginTime},'%Y-%m-%d')
AND DATE_FORMAT(s.add_time,'%Y-%m-%d') < DATE_FORMAT(#{vo.endTime},'%Y-%m-%d')
