SELECT
FROM shop_ship_address ssa WHERE ssa.sa_type = 0
AND ssa.sa_user_id = #{vo.saUserId}

SELECT s.*,d.area_Name,d.id as area_Id,d.parent_id FROM shop_ship_address s
LEFT JOIN dic_area d ON d.id=s.sa_area_id WHERE
s.id=#{id,jdbcType=BIGINT}

SELECT

FROM shop_ship_address ssa


		ssa.sa_type = #{saType}


		AND ssa.sa_user_id = #{saUserId}



			AND ssa.sa_default = #{saDefault}



ORDER BY ssa.sa_default desc, ssa.sa_sequence asc
