mapper.select:
select

	distinct


from wj_proposal_detailed




	order by ${orderByClause}


	limit ${limit}

mapper.insert:
insert into wj_proposal_detailed (id, proposal_title, proposal_content,
wj_proposal_id, create_time, update_time,
create_by, update_by, is_deleted
)
values (#{id,jdbcType=BIGINT}, #{proposalTitle,jdbcType=VARCHAR},
#{proposalContent,jdbcType=VARCHAR},
#{wjProposalId,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP},
#{createBy,jdbcType=BIGINT}, #{updateBy,jdbcType=BIGINT},
#{isDeleted,jdbcType=BIGINT}
)
