mapper.select:
SELECT *FROM ( SELECT s.*,o.selled_count FROM shop_store s, o2o_group_goods o WHERE s.id=o.store_id AND s.store_status=15 AND o.gg_status=0
AND s.store_recommend=#{store_recommend}
AND s.area_id IN
#{id}
ORDER BY o.selled_count DESC) t GROUP BY id ORDER BY selled_count DESC

SELECT s.store_name, s.store_ower, s.area_id, s.add_Time, s.id, us.name, s.store_telephone FROM shop_store s left join user_seller us on s.create_user_id = us.uid , user_owns o WHERE s.create_user_id = o.seller_user_id AND s.store_status = 1 AND s.delete_status = 0 AND o.own_operator_id = #{operatorId}
AND s.store_name LIKE CONCAT('%',#{storeName},'%')
ORDER BY s.add_Time DESC

SELECT s.* FROM shop_store s, user_owns o WHERE s.create_user_id = o.seller_user_id AND s.store_status = 15 AND s.delete_status = 0 AND s.id = #{storeId}
AND (SELECT count(0) FROM dic_area a WHERE a.id = s.area_id AND a.parent_id = #{operatorAreaId}) > 0
AND s.area_id = #{operatorAreaId}

SELECT
s.id,s.store_status,s.is_eepay,a.fail_reseaon,s.is_shop,s.is_o2o,a.fail_reseaon
FROM shop_store s LEFT JOIN shop_store_apply_info a ON a.store_id=s.id
WHERE create_user_id=#{uid,jdbcType=BIGINT}

	AND s.is_o2o = #{group}


	AND s.is_shop = #{shop}

SELECT ss.id, ss.store_address, ss.store_name, ss.group_discount, est.one AS est_one, est.two AS est_two, est.three AS est_three, est.five AS est_five, est.four AS est_four, sa.path AS store_info_path FROM shop_store ss
INNER JOIN o2o_group_floor_content gfc on ss.id=gfc.store_id
LEFT JOIN o2o_group_evaluation_store_total est ON ss.id = est.store_id LEFT JOIN user_album ua ON ss.create_user_id = ua.user_id AND ua.store_info_default = 1 LEFT JOIN sys_accessory sa ON ua.album_cover_id = sa.id WHERE ss.delete_Status=0
and gfc.floor_id=#{floorId}
and gfc.city_id=#{cityId}
order by gfc.add_time
limit 0,#{count}

SELECT store.store_address, store.area_id, store.store_telephone, s.invitation_code, store.id, store.store_name, store.store_banner_path, store.commission_rebate, store.store_cover_path, store.area_id, store.store_logo_path, c.coupon_pay_amount, c.charge_pay_amount, c.rebate_amount, store.profit_amount, store.is_o2o FROM shop_store store LEFT JOIN shop_comsume c ON c.store_id = store.id INNER JOIN sys_user s ON s.id = store.create_user_id WHERE store.create_user_id = #{uid,jdbcType=BIGINT}
AND store.is_o2o= #{group}
AND store.is_shop=#{shop}

SELECT ss.id, ss.add_Time, ss.delete_Status, ss.bank_account_name, ss.bank_c_account, ss.bank_line_num, ss.bank_name, ss.buygift_meal_end_Time, ss.combin_end_time, ss.domain_modify_count, ss.enoughreduce_meal_end_Time, ss.favorite_count, ss.gc_main_id, ss.group_meal_end_Time, ss.organization_code, ss.send_email_count, ss.send_sms_count, ss.store_address, ss.store_commission_amount, ss.store_credit, ss.store_decorate_old_theme, ss.store_decorate_theme, ss.store_email_count, ss.store_msn, ss.store_name, ss.store_ower, ss.store_ower_card, ss.store_payoff_amount, ss.store_qq, ss.store_recommend, ss.store_recommend_time, ss.store_sale_amount, ss.store_second_domain, ss.store_sms_count, ss.store_start_time, ss.store_status, ss.store_telephone, ss.store_ww, ss.store_zip, ss.store_info, ss.validity, ss.area_id, ss.bank_area_id, ss.bank_permit_image_path, ss.card_id, grade_id, ss.store_banner_path, ss.store_license_path, ss.store_logo_path, ss.is_shop, ss.is_o2o, ss.store_lat, ss.store_lon, ss.is_eepay, ss.create_user_id, ssg.grade_name, ssg.content, us. NAME, us.user_name, uc.nickname, sc.class_name, (SELECT login_name FROM `sys_user_account` sa WHERE sa.uid=uc.uid AND sa.account_type=2) AS mobile, sua.login_name, sua.uid, uo.id as operatorId, uo.operator_name, uo.operator_code, uo.phone as operatorPhone, uo.operator_area_id, ss.license_c_name, sai.fail_reseaon FROM shop_store ss LEFT JOIN shop_store_grade ssg ON ss.grade_id = ssg.id LEFT JOIN user_seller us ON us.store_id = ss.id inner JOIN user_customer uc ON uc.uid = ss.create_user_id LEFT JOIN sys_user_account sua ON (sua.uid = us.uid and sua.account_type = 2) LEFT JOIN dic_area a ON ss.area_id = a.id LEFT JOIN dic_area b ON b.id = a.parent_id LEFT JOIN shop_goods_class sc ON sc.id = ss.gc_main_id LEFT JOIN user_owns owns ON ss.create_user_id = owns.seller_user_id LEFT JOIN user_operator uo ON owns.own_operator_id = uo.id LEFT JOIN dic_area c ON uo.operator_area_id = c.id LEFT JOIN dic_area d ON d.id = c.parent_id LEFT JOIN shop_store_apply_info sai ON sai.store_id = ss.id
ss.store_status = #{vo.storeStatus}
and (ss.store_name like concat('%',trim(#{vo.storeName}),'%') or ss.license_c_name like concat('%',trim(#{vo.storeName}),'%') )
and ss.grade_id = #{vo.gradeId}
and ss.is_shop = #{vo.isShop}
and ss.is_o2o = #{vo.isO2o}
and ss.store_recommend = #{vo.storeRecommend}
and (b.parent_id = #{vo.areaId} or a.parent_id = #{vo.areaId})
and (b.parent_id = #{vo.operatorAreaId} or a.parent_id = #{vo.operatorAreaId})
and ss.gc_main_id = #{vo.gcMainId}
and ss.group_main_id = #{vo.groupMainId}
and uc.mobile like CONCAT('%',#{vo.mobile},'%')
and sua.login_name like CONCAT('%',#{vo.loginName},'%')
and uo.operator_code = #{vo.operatorCode}
and uo.operator_name like CONCAT('%',#{vo.operatorName},'%')
and uo.phone = #{vo.operatorPhone}
AND DATE_FORMAT(ss.add_time,'%Y-%m-%d') >= DATE_FORMAT(#{vo.beginTimeStr},'%Y-%m-%d')
AND DATE_FORMAT(ss.add_time,'%Y-%m-%d') < DATE_FORMAT(#{vo.endTimeStr},'%Y-%m-%d')
AND owns.own_type=#{vo.ownType}
order by ss.add_Time desc

